      call interp (period1(count1),period1(count2),c7(count1),c7(count2),
     +             specT,c7T,iflag)

 1011 period = specT

c     Set c1 (flag=0 for mega thrust at 20 km)
c            (flag=1 for intraslab at 55 km)
      if ( flag .eq. 0. ) then
        c1 = 0.3633
      elseif ( flag .eq. 1. ) then
        c1 = 0.9908
      else
C        write (*,*)') flag
        stop 99
      endif
      c2 = 1.4142
      c4 = 1.7818
      c5 = 0.554
      c8 = 3
      c9 = 10.
      c10 = 1.45
      c11 = -0.1

      lnY = c1 + c2*mag - c3T*alog(rupDist+c4*exp(c5*mag))
     1       + c6T + c7T*(c9-mag)**(c8)

      if ( mag .lt. 8.0 ) then
        sigma = c10 + c11*mag
      else
        sigma = c10 + c11*8.0
      endif

c     Convert from g to gal
      lnY = lnY + 6.89

