         c3T     = c3(1)
         c4T     = c4(1)
         c5T     = c5(1)
         c6T     = c6(1)
         c7T     = c7(1)
         sigT    = sig(1)
         c1sT     = c1s(1)
         c2sT     = c2s(1)
         c3sT     = c3s(1)
         c4sT     = c4s(1)
         c5sT     = c5s(1)
         c6sT     = c6s(1)
         c7sT     = c7s(1)
