     2         2.26,2.2,2.12,2.06,1.92,1.88,1.8,1.7,1.64,1.54,1.5,1.46 /

C First check for the PGA case (i.e., specT=0.0)
      nPer = 38
      if (specT .eq. 0.0) then
         period1 = period(1)
         a1T = a1(1)
         b1T = b1(1)
         c1T = c1(1)
         d1T = d1(1)
         a2T = a2(1)
         b2T = b2(1)
         c2T = c2(1)
         sig1T = sig1(1)
