        sigma_aRjb = sig_aRjb1T + (sig_aRjb2T - sig_aRjb1T)/(20.-5.) * (dist-5)
      elseif ( dist > 20 ) then
        sigma_aRjb = sig_aRjb2T
      endif

      sigma = SQRT(sigma_aMag**2 + sigma_aRjb**2)

      return
      end

