      call interp (period(count1),period(count2),sig_aRjb2(count1),sig_aRjb2(count2),
     +             specT,sig_aRjb2T,iflag)

 1011 period1 = specT

c     Set atten name
      attenName = 'Toro et al. Gulf (1996), MLg magnitude'

      rm = sqrt(dist*dist+c7T*c7T)

      if (rm .le. 100) then
         rmmax=0.0
      else
         rmmax = alog(rm/100.0)
