     1             + (a5T+a6T*(M-Mref2))*alog(sqrt(a7T*a7T+Rjb*Rjb))
      endif

C     Add Ztor term
      lnY = lnY + (a9T*ztor)/20.0

      sigma = 0.65

C     Convert ground motion to units of gals in natural log units.
      lnY = lnY + 4.6052

      period2 = period1

      return
      end

