C         write (*,*) 'Undefined Mechanism for BC Hydro Subduction Attenuation Model.'
C         write (*,*) 'Ftype = ', ftype
C         write (*,*) 'Check input fault file.'
         stop 99
      endif

C     Set sigma values to return
      sigma = sigsT
      tau = sigtT

c     Set SA to return
      lnSa = sum

      return
      end

