      data c9     /  0.49, 0.358, 0.000, 0.49, 0.49, 0.49, 0.49, 0.49, 0.49, 0.49, 0.49, 0.49,
     &               0.49, 0.49, 0.49, 0.49, 0.49, 0.49, 0.371, 0.154, 0.0,
