     &   0.596,0.601,0.557,0.518,0.488,0.479,0.471,0.467,0.464,0.456,
     &   0.466,0.466,0.476,0.473,0.462,0.427,0.407,0.401,0.386,0.373,
     &   0.322/
      data sigma2  / 0.613, 0.614, 0.613, 0.614, 0.62, 0.639, 0.656, 0.673, 0.692,
