      subroutine fEnx( x, Enx, n)

      Integer MaxIt, i, ii, nm1 As
      real expint, Eps, FpMin, Euler, Enx
      real a, b, c, d, del, Fact, H, psi

c      'PARAMETER (MAXIT=100,EPS=1.e-7,FPMIN=1.e-30,EULER=.5772156649)
       MaxIt = 100
       Eps = 0.0000001
       FpMin = 1E-30
       Euler = 0.5772156649

       nm1 = n - 1

      If (n .le. 0 .or. x .le. 0 ) Then

          write(*,*) "fEnx Input Error"
		  goto 99

		  elseif ( x .eq. 0 .And. n .eq.0 ) then
          write(*,*) "fEnx Input Error"
		  goto 99

		    elseif ( x .eq. 0 .And.  n .eq. 1) then
            write(*,*) "fEnx Input Error"
		    goto 99

      End If

      If ( n .eq. 0) Then
          expint = Exp(-x) / x
      Else

          If (x .eq. 0) Then
              expint = 1.0/ nm1
          Else

              If (x .ge. 1) Then
                  b = x + n
                  c = 1.0 / FpMin
                  d = 1.0 / b
                  H = d

                  Do i = 1 , MaxIt
                      a = -i * (nm1 + i)
                      b = b + 2.0
                      d = 1.0 / (a * d + b)
                      c = b + a / c
                      del = c * d
                      H = H * del

                      If (Abs(del - 1.0) <= Eps) Then
                          expint = H * Exp(-x)
                          Enx = expint
                          goto 99
                      End If
                   enddo

                  write(*,*) "fEnx Input Error"
                  goto 99

              Else

                  If (nm1 .ne. 0) Then
                      expint = 1.0 / nm1
                  Else
                      expint = -Log(x) - Euler
                  End If

                  Fact = 1.0

                  Do i = 1, MaxIt
                      Fact = -Fact * x / i

                      If (i .ne. nm1) Then
                          del = -Fact / (i - nm1)
                      Else
                          psi = -Euler

                          DO ii = 1, nm1
                              psi = psi + 1.0 / ii
                          enddo

                          del = Fact * (-Log(x) + psi)
                      End If

                      expint = expint + del

                      If (Abs(del) .le. Abs(expint) * Eps) Then
                          Enx = expint
                          goto 99
                      End If
                  enddo

                  write(*,*)  "fEnx Input Error"
                  goto 99

              End If

          End If

      End If



      Enx = expint


99    continue
      return
      End


